import '../styles/styles.css';
import React, { useState } from 'react';


const Container =(props)=> {

  const [expand, setExpand] = useState(props.data)


  if (!props.data || props.data.length < 0) {
      return "No Images Available"
  }
  console.log(props.data)

  const expandPanel = (id)=> {
    const active = [...props.data]
    console.log(active)
  }




  return (
    <div className="container">
      {props.data.map((item)=>{
        return (
          <div className= {`panel ${item.active ? 'expand' : ""}`}
          style = {{backgroundImage:`url(${item.image})`}} key = {item.id}
          onClick ={expandPanel}>
          <h2 className= {`'' ${item.active ? 'expand' : ""}`}
          >{item.title}</h2>
      </div>
        )
        })}
    </div>
  );
}

export default Container;
